import React from 'react';
import './Search.css';
import axios from 'axios';
import 'antd/dist/antd.css';
import { Input, Avatar, List, Card, Space } from 'antd';

const { Search } = Input;


class Symbols extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            query: '',
            results: {},
            loading: false,
            message: ''
        }
        this.cancel = '';
    }

    fetchSearchResults = (query) => {
        const SearchUrl = `https://api.stocktwits.com/api/2/streams/symbol/${query}.json`;

        if(this.cancel) {
            this.cancel.cancel();
        }

        this.cancel = axios.CancelToken.source();

        axios.get( SearchUrl, {
            cancelToken: this.cancel.token
        })
        .then(res => {
            console.warn(res.data.messages)
            const resultNotFoundMsg = !res.data.messages.length
                                    ? 'There are no more search results. Please try a new search.'
                                    : '';
            this.setState({
                results: res.data.messages,
                message: resultNotFoundMsg,
                loading: false
            })
        })
        .catch(error => {
            if(axios.isCancel(error) || error) {
                this.setState({
                    loading: false,
                    message: 'Failed to fetch the data. Please check network'
                })
            }
        })
    }

    handleOnInputChange = (event) => {
        const query = event.target.value;
        if(! query) {
            this.setState({
                query: query,
                results: {},
                message: ''
            })
        } else {
            this.setState({ 
                query: query,
                loading: true,
                message: '' }, () => {
                    this.fetchSearchResults(query);
         })
        }
    }

    renderSearchResults = () => {
        const { results } = this.state;
        // const item = results[index];
        if( Object.keys( results ).length && results.length ){
            return (
                <div className="results-container">
                    { results.map( result => {
                        return (
                            <Space direction="vertical">
                            <Card size="small" title="Small size card" >
                            <List.Item key={ result.id }>
                            <List.Item.Meta
                             avatar={<Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />}
                             title={<a href="https://ant.design"></a>}
                             description={result.body}
                            />
                    <div>{result.created_at}</div>
                  </List.Item>
                  </Card>
                  </Space>
                        )
                     }) }
                </div>
            )
        } 
    }

    render(){
        const { query } = this.state;
        console.warn(this.state)
        return(
            <div className="container">
                {/*Heading*/}
                <h2 className="heading">Live Search</h2>
                <Search
                    placeholder="input search text"
                    onSearch={query}
                    onChange={this.handleOnInputChange}
                    style={{ width: 200 }}
                />


                {/*Search Input*/}
                {/* <label className="search-label" htmlFor="search-input">
                    <input
                       type="text"
                       value={query}
                       name="query"
                       id="search-input"
                       placeholder="Search..."
                       onChange={this.handleOnInputChange}
                        />
                </label> */}
                {this.renderSearchResults()}

            </div>
        )
    }
}

export default Symbols;
