import React from 'react';
import './Search.css';
import axios from 'axios';
import 'antd/dist/antd.css';
import { Row, Col, Input, Avatar, Card, Space, Spin } from 'antd';
import { UserOutlined } from '@ant-design/icons';

const { Search } = Input;

class Symbols extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            query: '',
            results: {},
            loading: false,
            message: ''
        }
        this.cancel = '';
    }

    fetchSearchResults = (query) => {
        const SearchUrl = `https://api.stocktwits.com/api/2/streams/symbol/${query}.json`;

        if (this.cancel) {
            this.cancel.cancel();
        }

        this.cancel = axios.CancelToken.source();

        axios.get(SearchUrl, {
            cancelToken: this.cancel.token
        })
            .then(res => {
                console.warn(res.data.messages)
                const resultNotFoundMsg = !res.data.messages.length
                    ? 'There are no more search results. Please try a new search.'
                    : '';
                this.setState({
                    results: res.data.messages,
                    message: resultNotFoundMsg,
                    loading: false
                })
            })
            .catch(error => {
                if (axios.isCancel(error) || error) {
                    this.setState({
                        loading: false,
                        message: 'Failed to fetch the data. Please check network'
                    })
                }
            })
    }

    handleOnInputChange = (event) => {
        const query = event.target.value;
        if (!query) {
            this.setState({
                query: query,
                results: {},
                message: ''
            })
        } else {
            this.setState({
                query: query,
                loading: true,
                message: ''
            }, () => {
                this.fetchSearchResults(query);
            })
        }
    }

    renderSearchResults = () => {
        const { results } = this.state;
        // const item = results[index];
        if (Object.keys(results).length && results.length) {
            return (
                <div className="results-container">
                    {results.map(result => {
                        return (
                            <Space size={24} direction="horizontal">
                                <Card style={{ width: 575 }} key={result.id}>
                                    <Row>
                                        <Col span={8}>
                                            <Avatar shape="circle" size={44} src={result.user.avatar_url} icon={<UserOutlined />} />
                                        </Col>
                                        <Col span={8} pull={6}> </Col>
                                        <Col span={8}><b>@{result.user.name}</b> </Col>
                                    </Row>
                                    <Row>
                                        <Col span={24}>
                                            <p>{result.body}</p>

                                        </Col>
                                    </Row>
                                </Card>
                            </Space>
                        )
                    })}
                </div>
            )
        }
    }

    render() {
        const { query, loading, message } = this.state;
        console.warn(this.state)
        return (
            <div className="container">
                {/*Heading*/}
                <h2 className="heading">Live Search</h2>
                <Row>
                    <Col span={24} md={{ span: 12, offset: 6 }}>
                        <Search
                            placeholder="input search text"
                            onSearch={query}
                            size="large"
                            onChange={this.handleOnInputChange}
                        />
                    </Col>
                </Row>
                <Row>
                    <Col span={24} md={{ span: 12, offset: 6 }}>
                        {this.renderSearchResults()}
                        {message && <p className='message'>{message}</p>}
                        <div className={`search-loading ${loading ? 'show' : 'hide'}`}>
                            <Spin size="large" />
                        </div>
                    </Col>
                </Row>


            </div>
        )
    }
}

export default Symbols;
